import UIKit



class ConvertToPdfController: NSObject {
    var interactor: ConvertToPdfBusinessLogic?
    var router: (NSObjectProtocol & ConvertToPdfRoutingLogic & ConvertToPdfDataPassing)?
    var sourceRouter: ShowReportRouter?
    

    // MARK: Object lifecycle

    convenience init(sourceRouter: ShowReportRouter) {
        self.init()
        self.sourceRouter = sourceRouter
    }
    
    override init() {
        super.init()
        setup()
    }

  

    // MARK: Setup

    private func setup() {
        let controller = self
        let interactor = ConvertToPdfInteractor()
        let router = ConvertToPdfRouter()
        controller.interactor = interactor
        controller.router = router
        router.controller = controller
        router.dataStore = interactor
    }

   
    // MARK: - Convert to pdf

    func convertToPdf() {
        interactor!.convertToPdf(callback: { 
            self.router!.returnToSourceRouter()
        }) 
    }

}
