import UIKit

@objc protocol DisplayReportsRoutingLogic {
    func routeToShowReport(segue: UIStoryboardSegue?)
    func passSelectedItemToShowReport()
}

protocol DisplayReportsDataPassing {
    var dataStore: DisplayReportsDataStore? { get }
}

class DisplayReportsRouter: NSObject, DisplayReportsRoutingLogic, DisplayReportsDataPassing {
    weak var viewController: DisplayReportsViewController?
    var dataStore: DisplayReportsDataStore?
    var segue: UIStoryboardSegue?
  
    
    // MARK: Routing
  
    /*
     The segue is called before we can set the selected item in the interactor so just store the segue here
     */
    func routeToShowReport(segue: UIStoryboardSegue?) {
        self.segue = segue
    }

    func passSelectedItemToShowReport() {
        let destinationVC = (segue!.destination as! UINavigationController).viewControllers[0] as! ShowReportViewController
        var destinationDS = destinationVC.router!.dataStore!
        passDataToShowReport(source: dataStore!, destination: &destinationDS)
    }
  
    // MARK: Passing data
  
    func passDataToShowReport(source: DisplayReportsDataStore, destination: inout ShowReportDataStore) {
        destination.selectedItem = source.selectedItem
    }
}
