import UIKit

protocol DisplayMeetingGroupsForReportsPresentationLogic {
    func presentMeetingGroups(response: DisplayMeetingGroupsForReports.MeetingGroups.Response)
}

class DisplayMeetingGroupsForReportsPresenter: DisplayMeetingGroupsForReportsPresentationLogic {
    weak var viewController: DisplayMeetingGroupsForReportsDisplayLogic?

    // MARK: VIP
    
    func presentMeetingGroups(response: DisplayMeetingGroupsForReports.MeetingGroups.Response) {
        var meetingGroupNames = [String]()
        meetingGroupNames.append("Deleted groups")
        if let meetingGroups = response.meetingGroups {
            for meetingGroup in meetingGroups {
                let name = (meetingGroup.name ?? "")
                meetingGroupNames.append(name)
            }
        }
        let viewModel = DisplayMeetingGroupsForReports.MeetingGroups.ViewModel(meetingGroupNames: meetingGroupNames)
        viewController?.displayMeetingGroups(viewModel: viewModel)
    }
}
