import UIKit

@objc protocol ShowReportRoutingLogic {
    func routeToConvertToPdf()
    func returnFromConvertToPdf()
}

protocol ShowReportDataPassing {
    var dataStore: ShowReportDataStore? { get }
}

class ShowReportRouter: NSObject, ShowReportRoutingLogic, ShowReportDataPassing {
    weak var viewController: ShowReportViewController?
    var dataStore: ShowReportDataStore?
    var convertPdf: ConvertToPdfController?
    
    
  // MARK: Routing
  
    func routeToConvertToPdf() {
        convertPdf = ConvertToPdfController(sourceRouter: self)
        let destinationVC = convertPdf
        var destinationDS = destinationVC!.router!.dataStore!
        passDataToConvertToPdf(source: dataStore!, destination: &destinationDS)
        convertPdf?.convertToPdf()
    }
    
    func returnFromConvertToPdf() {
        let source = convertPdf
        let sourceDS = source!.router!.dataStore!
        receiveDataFromConvertToPdf(source: sourceDS, destination: &dataStore! )
        viewController!.showSharePopUp()
    }

    
  // MARK: Passing data
  
    func passDataToConvertToPdf(source: ShowReportDataStore, destination: inout ConvertToPdfDataStore) {
        destination.attText = source.attText
    }
    
    func receiveDataFromConvertToPdf(source:  ConvertToPdfDataStore, destination: inout ShowReportDataStore) {
        destination.pdfUrl = source.pdfUrl
    }
}
