import UIKit

protocol EditSubEntityPresentationLogic {
    func presentMembers(response: EditSubEntity.SubEntity.Response)
}


class EditSubEntityPresenter: EditSubEntityPresentationLogic {
    weak var viewController: EditSubEntityDisplayLogic?

    // MARK: Do something

    func presentMembers(response: EditSubEntity.SubEntity.Response) {
        var memberString: String?
        let members = response.members
        if members != nil {
            memberString = String()
            for member in members! {
                if memberString!.count > 0 {
                    memberString!.append(", ")
                }
                memberString!.append(member.firstName! + member.lastName!)
            }
        }
        let viewModel = EditSubEntity.SubEntity.ViewModel(memberNames: memberString)
        viewController?.displayMemberNames(viewModel: viewModel)
    }
}
