import UIKit

@objc protocol RemoveSubEntityRoutingLogic {
    func returnToSource(source: EditSubEntityViewController)
}

protocol RemoveSubEntityDataPassing {
    var dataStore: RemoveSubEntityDataStore? { get }
}

class RemoveSubEntityRouter: NSObject, RemoveSubEntityRoutingLogic, RemoveSubEntityDataPassing {
    weak var controller: RemoveSubEntityController?
    var dataStore: RemoveSubEntityDataStore?
  
    
    // MARK: Routing
    
    func returnToSource(source: EditSubEntityViewController) {
        var destinationDS = controller?.sourceVC?.router?.dataStore
        passDataToEditSubEntityDataStore(source: dataStore!, destination: &destinationDS!)
        source.router!.returnFromRemovingSubEntity()
    }
    
    
    // MARK: Passing data
    
    func passDataToEditSubEntityDataStore(source: RemoveSubEntityDataStore, destination: inout EditSubEntityDataStore) {
        destination.entity = source.entity
    }
}
