import UIKit

protocol SelectMembersPresentationLogic {
    func presentMembers(response: SelectMembers.Members.Response)
}

class SelectMembersPresenter: SelectMembersPresentationLogic {
    weak var viewController: SelectMembersDisplayLogic?

    // MARK: Do something

    func presentMembers(response: SelectMembers.Members.Response) {
        var memberNames = [String]()
        if let members = response.members {
            for member in members {
                let name = (member.title ?? "") + " " + member.firstName! + " " + member.lastName!
                memberNames.append(name)
            }
        }
        let viewModel = SelectMembers.Members.ViewModel(memberNames: memberNames, selectedRows: nil)
        viewController?.displayMemberNames(viewModel: viewModel) 
    }
}
