import UIKit

@objc protocol EditEventRoutingLogic {
    func returnToSource(source: DisplayEventsViewController)
    func returnFromRemovingEvent()
    func navigateToRemoveEvent()
}

protocol EditEventDataPassing {
    var dataStore: EditEventDataStore? { get }
}

class EditEventRouter: NSObject, EditEventRoutingLogic, EditEventDataPassing {
    weak var viewController: EditEventViewController?
    var dataStore: EditEventDataStore?
    var removeCon: RemoveEventController?
  
    // MARK: Routing
    
    func returnToSource(source: DisplayEventsViewController) {
        source.router?.returnFromEditingEvent()
    }
    
    func returnFromRemovingEvent() {
        returnToSource(source: viewController!.sourceVC!)
    }
    
    func navigateToRemoveEvent() {
        removeCon = RemoveEventController(source: viewController!)
        var destinationDS = removeCon?.router?.dataStore
        passDataToRemoveEventDataStore(source: dataStore!, destination: &destinationDS!)
        removeCon?.removeEvent()
    }
    
    func passDataToRemoveEventDataStore(source: EditEventDataStore, destination: inout RemoveEventDataStore) {
        destination.event = source.event
    }
}
