import UIKit



class RemoveEventController: NSObject {
    var interactor: RemoveEventBusinessLogic?
    var router: (NSObjectProtocol & RemoveEventRoutingLogic & RemoveEventDataPassing)?
    var sourceVC: EditEventViewController?

    
    // MARK: Object lifecycle
    
    convenience init(source: EditEventViewController) {
        self.init()
        self.sourceVC = source
        setup()
    }
    
    
    override init() {
        super.init()
    }
    
    
    deinit {
        print("RemoveEventController: deinitialising")
    }


    // MARK: Setup

    private func setup() {
        let controller = self
        let interactor = RemoveEventInteractor()
        let router = RemoveEventRouter()
        controller.interactor = interactor
        controller.router = router
        router.controller = controller
        router.dataStore = interactor
    }


  func removeEvent() {
        interactor?.removeEvent(callback: { 
            self.router!.returnToSource(source: self.sourceVC!)
        })
    }
}
