import UIKit

@objc protocol RemoveMeetingGroupRoutingLogic {
    func returnToSource(source: EditMeetingGroupViewController)
}

protocol RemoveMeetingGroupDataPassing {
    var dataStore: RemoveMeetingGroupDataStore? { get }
}

class RemoveMeetingGroupRouter: NSObject, RemoveMeetingGroupRoutingLogic, RemoveMeetingGroupDataPassing {
    weak var controller: RemoveMeetingGroupController?
    var dataStore: RemoveMeetingGroupDataStore?
  
    
    // MARK: Routing
    
    func returnToSource(source: EditMeetingGroupViewController) {
        var destinationDS = controller?.sourceVC?.router?.dataStore
        passDataToEditMeetingGroupDataStore(source: dataStore!, destination: &destinationDS!)
        source.router!.returnFromRemovingMeetingGroup()
    }
    
    
    // MARK: Passing data
    
    func passDataToEditMeetingGroupDataStore(source: RemoveMeetingGroupDataStore, destination: inout EditMeetingGroupDataStore) {
        destination.entity = source.entity
        destination.meetingGroup = source.meetingGroup
    }
}
