import UIKit

@objc protocol RemoveMemberRoutingLogic {
   func returnToSource(source: EditMemberViewController)
}

protocol RemoveMemberDataPassing {
    var dataStore: RemoveMemberDataStore? { get }
}

class RemoveMemberRouter: NSObject, RemoveMemberRoutingLogic, RemoveMemberDataPassing {
    weak var controller: RemoveMemberController?
    var dataStore: RemoveMemberDataStore?
  
    
    // MARK: Routing
  
    func returnToSource(source: EditMemberViewController) {
        var destinationDS = controller?.sourceVC?.router?.dataStore
        passDataToEditMemberDataStore(source: dataStore!, destination: &destinationDS!)
        source.router!.returnFromRemovingMember() 
    }
  
    
    // MARK: Passing data
  
    func passDataToEditMemberDataStore(source: RemoveMemberDataStore, destination: inout EditMemberDataStore) {
        destination.entity = source.entity
        destination.member = source.member
    }
}
