import UIKit

@objc protocol ShowHelpRoutingLogic {
  func returnFromHelp()
}

protocol ShowHelpDataPassing {
    var dataStore: ShowHelpDataStore? { get }
}

class ShowHelpRouter: NSObject, ShowHelpRoutingLogic, ShowHelpDataPassing {
    weak var viewController: ShowHelpViewController?
    var dataStore: ShowHelpDataStore?
  
  // MARK: Routing
  
    func returnFromHelp() {
        viewController!.source!.router!.returnFromShowHelpController()
    }
}
